--  DDL for View CONTRACT_TYPE_DETAIL
--------------------------------------------------------

  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "CUOIKY"."CONTRACT_TYPE_DETAIL" ("CT_ID", "CONTRACT_TYPE_NAME", "TIME_KEEPING_METHOD_NAME", "COUNT") AS 
  SELECT CT.CT_ID, CONTRACT_TYPE_NAME, TIME_KEEPING_METHOD_NAME, COALESCE(COUNT, 0) AS COUNT FROM (SELECT CT_ID, CONTRACT_TYPE_NAME, 
TKM_ID FROM CONTRACT_TYPE) CT LEFT JOIN (SELECT CT_ID, COUNT(STAFF_ID) AS COUNT FROM STAFF GROUP BY CT_ID) S ON CT.CT_ID = S.CT_ID JOIN
(SELECT TKM_ID, TIME_KEEPING_METHOD_NAME FROM TIME_KEEPING_METHOD) TKM ON CT.TKM_ID = TKM.TKM_ID
;
