--  DDL for View COUNT_BENEFIT
--------------------------------------------------------

  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "CUOIKY"."COUNT_BENEFIT" ("BN_ID", "BENEFIT_NAME", "AMOUNT", "STAFF_QUANTITY", "TOTALAMOUNT") AS 
  SELECT BN.BN_ID, BENEFIT_NAME, AMOUNT, COALESCE(STAFF_QUANTITY, 0) AS STAFF_QUANTITY, COALESCE(STAFF_QUANTITY * AMOUNT, 0) AS TotalAmount
FROM (SELECT BN_ID, BENEFIT_NAME, AMOUNT FROM BENEFIT) BN LEFT JOIN (SELECT BN_ID,  COUNT(STAFF_ID) AS STAFF_QUANTITY
FROM BENEFIT_DETAIL GROUP BY BN_ID) BND ON BN.BN_ID = BND.BN_ID
GROUP BY BN.BN_ID, BENEFIT_NAME, AMOUNT, STAFF_QUANTITY
;
--------------------------------------------------------
--  DDL for View DAY_OFF_USED
--------------------------------------------------------

  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "CUOIKY"."DAY_OFF_USED" ("WS_ID", "WORK_DATE", "STAFF_ID", "DATE_OFF", "FULL_NAME", "POSITION_NAME", "DEPARTMENT_NAME", "NOTE") AS 
  SELECT WS.WS_ID, WORK_DATE, WSD.STAFF_ID, DATE_OFF, FULL_NAME, POSITION_NAME, DEPARTMENT_NAME, NOTE
FROM (SELECT WS_ID, WORK_DATE FROM WORK_SCHEDULE) WS JOIN (SELECT WS_ID, STAFF_ID, NOTE, DATE_OFF FROM WORK_SCHEDULE_DETAIL
WHERE DATE_OFF = 1) WSD ON WS.WS_ID = WSD.WS_ID
JOIN (SELECT STAFF_ID, PS_ID, LAST_NAME || ' ' || MIDDLE_NAME || ' ' || FIRST_NAME AS FULL_NAME FROM STAFF) S ON WSD.STAFF_ID = S.STAFF_ID
JOIN (SELECT PS_ID, DP_ID, POSITION_NAME FROM POSITION) PS ON PS.PS_ID = S.PS_ID
JOIN (SELECT DP_ID, DEPARTMENT_NAME FROM DEPARTMENT) DP ON DP.DP_ID = PS.DP_ID
;
