--  DDL for View DEPARTMENT_DETAIL
--------------------------------------------------------

  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "CUOIKY"."DEPARTMENT_DETAIL" ("DP_ID", "DEPARTMENT_NAME", "COUNT") AS 
  SELECT DP.DP_ID, DEPARTMENT_NAME, COALESCE(SUM(COUNT_DP),0) AS COUNT FROM (SELECT DP_ID, DEPARTMENT_NAME FROM DEPARTMENT) DP LEFT JOIN 
(SELECT PS_ID, DP_ID FROM POSITION) PS ON DP.DP_ID = PS.DP_ID LEFT JOIN (SELECT PS_ID , COUNT(STAFF_ID) AS COUNT_DP FROM STAFF
GROUP BY PS_ID) S ON S.PS_ID = PS.PS_ID GROUP BY DP.DP_ID, DEPARTMENT_NAME
