--  DDL for View SALARY
--------------------------------------------------------

  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "CUOIKY"."SALARY" ("MS_ID", "STAFF_ID", "FULL_NAME", "DEPARTMENT_NAME", "POSITION_NAME", "BASIC_SALARY", "TOTAL_WORK_HOURS", "TOTAL_BENEFIT", "SALARY", "MONTH") AS 
  SELECT MS.MS_ID, MS.STAFF_ID, FULL_NAME, DEPARTMENT_NAME, POSITION_NAME, BASIC_SALARY, TOTAL_WORK_HOURS, TOTAL_BENEFIT, (BASIC_SALARY * TOTAL_WORK_HOURS + TOTAL_BENEFIT) AS SALARY, MONTH
FROM (SELECT STAFF_ID, PS_ID, LAST_NAME || ' ' || MIDDLE_NAME || ' ' || FIRST_NAME AS FULL_NAME FROM STAFF) S JOIN
(SELECT PS_ID, DP_ID, POSITION_NAME FROM POSITION) PS ON S.PS_ID = PS.PS_ID JOIN (SELECT DP_ID, DEPARTMENT_NAME FROM DEPARTMENT) DP
ON PS.DP_ID = DP.DP_ID JOIN (SELECT * FROM MONTH_SALARY_DETAIL) MS ON MS.STAFF_ID = S.STAFF_ID JOIN (SELECT * FROM MONTH_SALARY) SL ON
MS.MS_ID = SL.MS_ID
;
