--  DDL for View STAFF_TIME_KEEPING
--------------------------------------------------------

  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "CUOIKY"."STAFF_TIME_KEEPING" ("WS_ID", "WORK_DATE", "STAFF_ID", "SHIFT_ID", "FULL_NAME", "POSITION_NAME", "DEPARTMENT_NAME", "SHIFT_NAME", "CHECK_IN", "CHECK_OUT") AS 
  SELECT WS.WS_ID, WORK_DATE, TK.STAFF_ID, TK.SHIFT_ID, FULL_NAME, POSITION_NAME, DEPARTMENT_NAME, SHIFT_NAME, CHECK_IN, CHECK_OUT
FROM (SELECT WS_ID, WORK_DATE FROM WORK_SCHEDULE) WS JOIN (SELECT WS_ID, STAFF_ID FROM WORK_SCHEDULE_DETAIL) WSD ON WS.WS_ID = WSD.WS_ID
JOIN (SELECT WS_ID, STAFF_ID, SHIFT_ID, CHECK_IN, CHECK_OUT FROM TIME_KEEPING) TK ON WS.WS_ID = TK.WS_ID
JOIN (SELECT SHIFT_ID, SHIFT_NAME FROM SHIFT) SH ON SH.SHIFT_ID = TK.SHIFT_ID
JOIN (SELECT STAFF_ID, PS_ID, LAST_NAME || ' ' || MIDDLE_NAME || ' ' || FIRST_NAME AS FULL_NAME FROM STAFF) S ON WSD.STAFF_ID = S.STAFF_ID
JOIN (SELECT PS_ID, DP_ID, POSITION_NAME FROM POSITION) PS ON PS.PS_ID = S.PS_ID
JOIN (SELECT DP_ID, DEPARTMENT_NAME FROM DEPARTMENT) DP ON DP.DP_ID = PS.DP_ID
WHERE WSD.STAFF_ID = TK.STAFF_ID
;
