       EXTRACT(YEAR FROM SYSDATE) THEN
            RAISE_APPLICATION_ERROR(-20001, 'KHÔNG THỂ THÊM LỊCH TRONG QUÁ KHỨ');
        END IF;
        SELECT EXTRACT(DAY FROM LAST_DAY(TO_DATE(in_Month,'MM/YYYY'))) INTO totalDay FROM DUAL;  
        FOR i IN 1..totalDay LOOP      
            checkDate := CONCAT(CONCAT(i, '/'), in_Month);
            dayOfWeek := TO_NUMBER(TO_CHAR(TO_DATE(checkDate, 'DD/MM/YYYY'), 'D'));

             IF dayOfWeek != 1 AND dayOfWeek != 7 AND TRUNC(TO_DATE(checkDate, 'DD/MM/YYYY')) >= TRUNC(SYSDATE) THEN              
            END IF;
        END LOOP;

        FOR staff_rec IN (SELECT STAFF_ID FROM STAFF) LOOP
            FOR ws_rec IN (SELECT WS_ID FROM WORK_SCHEDULE
                         WHERE EXTRACT(MONTH FROM WORK_DATE) = EXTRACT(MONTH FROM TO_DATE(in_Month, 'MM/YYYY')) AND
