    PROCEDURE AUTO_UPDATE_MONTH_SALARY(in_Month VARCHAR2) IS
        checkMonth VARCHAR2(20);
        msID VARCHAR(20);
    BEGIN

        IF EXTRACT(MONTH FROM SYSDATE) > EXTRACT(MONTH FROM (TO_DATE(in_Month, 'MM/YYYY'))) AND 
        EXTRACT(YEAR FROM SYSDATE) >= EXTRACT(YEAR FROM (TO_DATE(in_Month, 'MM/YYYY'))) THEN
            RAISE_APPLICATION_ERROR(-20001, 'KHÔNG THỂ CẬP NHẬT LƯƠNG TRONG QUÁ KHỨ');
        END IF;    

        BEGIN
            SELECT MONTH INTO checkMonth FROM MONTH_SALARY WHERE MONTH = in_Month;
            EXCEPTION WHEN NO_DATA_FOUND THEN
                checkMonth := NULL;
        END;

        IF checkMonth IS NULL THEN
            SELECT MS_ID INTO msID FROM MONTH_SALARY WHERE MONTH = TO_CHAR(SYSDATE, 'MM/YYYY');
            FOR rec IN (SELECT STAFF_ID, BASIC_SALARY FROM STAFF) LOOP
                COALESCE(WORK_SCHEDULE_METHOD.CACUALTE_TOTAL_HOUR(checkMonth, rec.STAFF_ID), 0), WORK_SCHEDULE_METHOD.TOTAL_STAFF_BENEFIT(rec.STAFF_ID));
