  CREATE OR REPLACE NONEDITIONABLE PACKAGE BODY "CUOIKY"."WORK_SCHEDULE_METHOD" IS
    
    FUNCTION CACUALTE_TOTAL_HOUR(in_Month VARCHAR2, in_StaffID VARCHAR2) RETURN NUMBER IS
        TotalHour NUMBER := 0;
        temp NUMBER := 0;
        dayOff NUMBER := 0;
    BEGIN     
        SELECT COALESCE(SUM(EXTRACT(HOUR FROM (CHECK_OUT - CHECK_IN)) * 60 + EXTRACT(MINUTE FROM (CHECK_OUT - CHECK_IN)) + 
        EXTRACT(SECOND FROM (CHECK_OUT - CHECK_IN))/60)/60,0) * SALARY_COEFFICIENT INTO TotalHour
        FROM (SELECT WS_ID, STAFF_ID, SHIFT_ID, CHECK_IN, CHECK_OUT FROM TIME_KEEPING WHERE STAFF_ID = in_StaffID) TK JOIN 
        (SELECT WS_ID, WORK_DATE FROM WORK_SCHEDULE WHERE  TO_CHAR(TO_DATE(WORK_DATE), 'MM/YYYY') = in_Month) WS ON 
        TK.WS_ID = WS.WS_ID JOIN (SELECT SHIFT_ID, ST_ID FROM SHIFT) SH ON SH.SHIFT_ID = TK.SHIFT_ID
        JOIN (SELECT ST_ID, SALARY_COEFFICIENT FROM SHIFT_TYPE) ST ON ST.ST_ID = SH.ST_ID
        GROUP BY SALARY_COEFFICIENT;

        SELECT COALESCE(SUM(EXTRACT(HOUR FROM (END_TIME - BEGIN_TIME)) * 60 + EXTRACT(MINUTE FROM (END_TIME - BEGIN_TIME)) +  
        EXTRACT(SECOND FROM (END_TIME - BEGIN_TIME))/60)/60/2,0) INTO temp
        FROM (SELECT WS_ID, STAFF_ID, SHIFT_ID FROM TIME_KEEPING WHERE STAFF_ID = in_StaffID) TK JOIN (SELECT WS_ID, WORK_DATE FROM WORK_SCHEDULE 
        WHERE TO_CHAR(TO_DATE(WORK_DATE), 'MM/YYYY') = in_Month) WS ON TK.WS_ID = WS.WS_ID
        JOIN (SELECT WS_ID, STAFF_ID, DATE_OFF FROM WORK_SCHEDULE_DETAIL WHERE DATE_OFF = 1 AND STAFF_ID = in_StaffID) WSD 
        ON WSD.WS_ID = WS.WS_ID JOIN (SELECT SHIFT_ID, ST_ID, BEGIN_TIME, END_TIME FROM SHIFT WHERE ST_ID = 'ST_0000000001')
        SH ON SH.SHIFT_ID = TK.SHIFT_ID;
        dayOff := temp;  

        TotalHour := TotalHour + dayOff;

        RETURN TotalHour;
    END CACUALTE_TOTAL_HOUR;

    FUNCTION TOTAL_STAFF_BENEFIT(in_StaffID VARCHAR2) RETURN NUMBER IS
        TotalBenefit NUMBER := 0;
    BEGIN
        SELECT COALESCE(SUM(AMOUNT),0) INTO TotalBenefit
        FROM (SELECT BN_ID, STAFF_ID FROM BENEFIT_DETAIL WHERE STAFF_ID = in_StaffID) BD JOIN (SELECT BN_ID, AMOUNT FROM BENEFIT ) 
        B ON B.BN_ID = BD.BN_ID;    
        RETURN TotalBenefit;
    END TOTAL_STAFF_BENEFIT;   


    PROCEDURE TIME_KEEPING_CHECK(in_StaffID VARCHAR2) IS
        checkStaff VARCHAR(20);
        countShift NUMBER := 0;
        tkmID VARCHAR2(20);
        previousShift VARCHAR(20);
        timeNow INTERVAL DAY TO SECOND;
        checkTime INTERVAL DAY TO SECOND;
        previousCheck INTERVAL DAY TO SECOND := NULL;
        checkFlag NUMBER := 0;
    BEGIN
        BEGIN
            SELECT STAFF_ID INTO checkStaff FROM STAFF
            WHERE STAFF_ID = in_StaffID;

            EXCEPTION WHEN NO_DATA_FOUND THEN
                RAISE_APPLICATION_ERROR(-20001, 'MÃ NHÂN VIÊN KHÔNG HỢP LỆ');
        END;

        SELECT COUNT(STAFF_ID) INTO countShift FROM WORK_SCHEDULE_DETAIL WSD JOIN WORK_SCHEDULE WS ON WS.WS_ID = WSD.WS_ID
        WHERE WSD.STAFF_ID = in_StaffID AND TRUNC(WS.WORK_DATE) = TRUNC(SYSDATE); 

        timeNow := TO_DSINTERVAL('0 ' || TO_CHAR(SYSDATE, 'HH24:MI:SS'));

        IF countShift = 0 THEN
            RAISE_APPLICATION_ERROR(-20001, 'NHÂN VIÊN KHÔNG CÓ CA LÀM TRONG NGÀY ' || TRUNC(SYSDATE));

        ELSE
            SELECT TKM.TKM_ID INTO tkmID FROM STAFF S JOIN CONTRACT_TYPE CT ON S.CT_ID = CT.CT_ID JOIN TIME_KEEPING_METHOD TKM ON CT.TKM_ID = TKM.TKM_ID
            WHERE STAFF_ID = in_StaffID;

            FOR rec IN (SELECT TK.WS_ID, TK.SHIFT_ID, CHECK_IN, CHECK_OUT, BEGIN_TIME, END_TIME, SHIFT_NAME FROM
                    (SELECT WS_ID, STAFF_ID, SHIFT_ID, CHECK_IN, CHECK_OUT FROM TIME_KEEPING WHERE STAFF_ID = in_StaffID) 
                    TK JOIN (SELECT WS_ID, WORK_DATE FROM WORK_SCHEDULE WHERE TRUNC(WORK_DATE) = TRUNC(SYSDATE)) WS 
                    ON TK.WS_ID = WS.WS_ID JOIN (SELECT SHIFT_ID, SHIFT_NAME, BEGIN_TIME, END_TIME FROM SHIFT) SH 
                    ON TK.SHIFT_ID = SH.SHIFT_ID) LOOP

                IF rec.CHECK_IN IS NOT NULL AND rec.CHECK_OUT IS NULL AND timeNow < rec.BEGIN_TIME THEN
                    RAISE_APPLICATION_ERROR(-20001, 'VẪN CHƯA VÀO CA '|| rec.SHIFT_NAME);
                END IF;

                IF tkmID = 'TKM_0000000001' THEN
                    IF rec.CHECK_IN IS NULL AND timeNow < rec.END_TIME THEN 
                        IF timeNow <= rec.BEGIN_TIME THEN
                            checkTime := rec.BEGIN_TIME;
                        ELSE
                            checkTime := timeNow ;
                        END IF;
                        UPDATE TIME_KEEPING SET CHECK_IN = checkTime
                        WHERE WS_ID = rec.WS_ID AND SHIFT_ID = rec.SHIFT_ID AND STAFF_ID = in_StaffID;
                        EXIT;                
                    END IF;

                    IF rec.CHECK_IN IS NOT NULL AND rec.CHECK_OUT IS NULL THEN
                        checkFlag := 1;
                        IF timeNow > rec.END_TIME THEN
                            checkTime := rec.END_TIME;
                        ELSE
                            checkTime := timeNow;                    
                        END IF;
                        UPDATE TIME_KEEPING SET CHECK_OUT = checkTime
                        WHERE WS_ID = rec.WS_ID AND SHIFT_ID = rec.SHIFT_ID AND STAFF_ID = in_StaffID;
                    END IF;

                    IF timeNow >= rec.END_TIME AND rec.CHECK_IN IS NULL AND checkFlag = 1 THEN
                        UPDATE TIME_KEEPING SET CHECK_IN = rec.BEGIN_TIME
                        WHERE WS_ID = rec.WS_ID AND SHIFT_ID = rec.SHIFT_ID AND STAFF_ID = in_StaffID;
                    END IF;

                    IF timeNow >= rec.END_TIME AND rec.CHECK_OUT IS NULL AND checkFlag = 1 THEN 
                        UPDATE TIME_KEEPING SET CHECK_OUT = rec.END_TIME
                        WHERE WS_ID = rec.WS_ID AND SHIFT_ID = rec.SHIFT_ID AND STAFF_ID = in_StaffID;
                    END IF;
                END IF;

                IF tkmID = 'TKM_0000000002' THEN      
                    IF rec.CHECK_IN IS NULL AND timeNow < rec.END_TIME AND previousCheck IS NULL THEN 
                        IF timeNow <= rec.BEGIN_TIME THEN
                            checkTime := rec.BEGIN_TIME;
                        ELSE
                            checkTime := timeNow ;
                        END IF;
                        UPDATE TIME_KEEPING SET CHECK_IN = checkTime
                        WHERE WS_ID = rec.WS_ID AND SHIFT_ID = rec.SHIFT_ID AND STAFF_ID = in_StaffID;
                        EXIT;                
                    END IF;

                    IF rec.CHECK_IN IS NOT NULL AND rec.CHECK_OUT IS NULL THEN        
                        previousCheck := timeNow;
                        previousShift := rec.SHIFT_ID;
                        IF timeNow > rec.END_TIME THEN
                            checkTime := rec.END_TIME;
                        ELSE
                            checkTime := timeNow;                    
                        END IF;
                        UPDATE TIME_KEEPING SET CHECK_OUT = checkTime
                        WHERE WS_ID = rec.WS_ID AND SHIFT_ID = rec.SHIFT_ID AND STAFF_ID = in_StaffID;          
                        CONTINUE;
                    END IF;  

                    IF previousCheck IS NOT NULL THEN
                        IF rec.BEGIN_TIME <= previousCheck THEN
                            UPDATE TIME_KEEPING SET CHECK_OUT = NULL
                            WHERE WS_ID = rec.WS_ID AND SHIFT_ID = previousShift AND STAFF_ID = in_StaffID;

                            IF timeNow <= rec.BEGIN_TIME THEN
                            checkTime := rec.BEGIN_TIME;
                            ELSE
                                checkTime := timeNow ;
                            END IF;
                            UPDATE TIME_KEEPING SET CHECK_IN = checkTime
                            WHERE WS_ID = rec.WS_ID AND SHIFT_ID = rec.SHIFT_ID AND STAFF_ID = in_StaffID;
                            EXIT;          
                        END IF;      
                    END IF;
                END IF;
            END LOOP;
        END IF;
        AUTO_METHOD.AUTO_UPDATE_MONTH_SALARY_DETAIL(TO_CHAR(SYSDATE, 'MM/YYYY'));
    END TIME_KEEPING_CHECK;
END WORK_SCHEDULE_METHOD;

